#include <stdlib.h>
#include <stdio.h>
#include "stack.h"

typedef struct MatchingBrackets {
    const char* bracket;
    const char* matchingBracket;
} MatchingBrackets;

int compare (const MatchingBrackets* a, const MatchingBrackets* b) {
    const char* aBracket = (*a).bracket;
    const char* bBracket = (*b).bracket;

    return (aBracket > bBracket) - (aBracket < bBracket);
}

// Counts number of brackets in code.
int countBrackets(const char* code) {
    const char* ip;
    int bracketCount = 0;

    for (ip = code; *ip != '\0'; ip++) {
        switch (*ip) {
            case '[':
                bracketCount++;
                break;
            default:
                break;
        }
    }

    return bracketCount;
}

// Generates bracket location array.
MatchingBrackets* generateBracketLocations(const char* code, int bracketCount) {
    MatchingBrackets* locations = malloc(bracketCount * sizeof(MatchingBrackets));
    const char* ip;
    int bracketIndex = 0;

    Stack bracketStack = createStack(bracketCount);

    for (ip = code; *ip != '\0'; ip++) {
        switch (*ip) {
            case '[':
                push(&bracketStack, ip);
                break;
            case ']':
                locations[bracketIndex].bracket = pop(&bracketStack);
                locations[bracketIndex].matchingBracket = ip;
                bracketIndex++;
                break;
            default:
                break;
        }
    }

    if (!isEmpty(&bracketStack)) {
        return NULL;
    }

    qsort(locations, bracketCount, sizeof(*locations), compare);

    return locations;
}

// Binary search through bracketLocations.
const char* goToMatchingBracket(int bracketCount, MatchingBrackets* bracketLocations, const char* target) {
    int start = 0;
    int end = bracketCount - 1;
    
    while (start <= end) {
        int middle = (start + end) / 2;
        if (bracketLocations[middle].bracket == target) {
            return bracketLocations[middle].matchingBracket;
        }
        else if (bracketLocations[middle].bracket < target) {
            start = middle + 1;
        }
        else if (bracketLocations[middle].bracket > target) {
            end = middle - 1;
        }
    }

    return NULL;
}

// Main interpreter.
void interpret(const char* code, int bracketCount, MatchingBrackets* bracketLocations) {
    unsigned char mem[30000] = {0};
    unsigned char* ptr = mem;

    Stack bracketStack = createStack(bracketCount);

    const char* ip;

    for (ip = code; *ip != '\0'; ip++) {
        switch (*ip) {
            case '>':
                ptr++;
                break;
            case '<':
                ptr--;
                break;
            case '+':
                (*ptr)++;
                break;
            case '-':
                (*ptr)--;
                break;
            case '.':
                putchar(*ptr);
                break;
            case ',':
                *ptr = getchar();
                break;
            case '[':
                if (*ptr == 0) {
                    ip = goToMatchingBracket(bracketCount, bracketLocations, ip);
                }
                else {
                    push(&bracketStack, ip);
                }
                break;
            case ']':
                if (*ptr != 0) {
                    ip = peek(&bracketStack);
                }
                else {
                    pop(&bracketStack);
                }
                break;
            default:
                break;
        }
    }
}

